mapper.select:
select

from log.mm_log


        id = #{mmLog.id,jdbcType=NUMERIC}


        interface_name like concat('%', #{mmLog.interfaceName}, '%')


        request_params = #{mmLog.requestParams,jdbcType=VARCHAR}


        response_params = #{mmLog.responseParams,jdbcType=NUMERIC}


        status_code = #{mmLog.statusCode,jdbcType=NUMERIC}


        client_ip like concat('%', #{mmLog.clientIp}, '%')


        server_ip like concat('%', #{mmLog.serverIp}, '%')


ORDER BY create_date asc

select
count(1)
from log.mm_log


        id = #{mmLog.id,jdbcType=NUMERIC}


        interface_name = #{mmLog.interfaceName,jdbcType=VARCHAR}


        request_params = #{mmLog.requestParams,jdbcType=VARCHAR}


        response_params = #{mmLog.responseParams,jdbcType=NUMERIC}


        status_code = #{mmLog.statusCode,jdbcType=NUMERIC}


        client_ip = #{mmLog.clientIp,jdbcType=VARCHAR}


        server_ip = #{mmLog.serverIp,jdbcType=VARCHAR}
